import { Component } from '@angular/core';
import { FormControl,Validators,FormGroup } from '@angular/forms';
import { LogindataService } from '../../Services/logindata.service';
import { Router } from '@angular/router';
import { MatDialog } from '@angular/material/dialog';
@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

constructor(private loginservice:LogindataService,private router:Router,private dialog:MatDialog){

}

  hide = true;

  loginform=new FormGroup({
    email:new FormControl('',[Validators.required,Validators.email]),
    password:new FormControl('',[Validators.required,Validators.maxLength(20),Validators.minLength(8)])
})


userlogin(){
this.loginservice.getUserData(this.loginform.value);
// this.loginform.reset();
this.router.navigate(['']);

}

closedialog(){
  const timeout=3000;
  setTimeout(()=>{
    this.dialog.closeAll();
  },timeout)

}

}
