import { Component, OnInit, } from '@angular/core';
import { Service1Service } from '../../Services/service1.service';


@Component({
  selector: 'app-body',
  templateUrl: './body.component.html',
  styleUrls: ['./body.component.css']
})
export class BodyComponent implements OnInit {

  constructor(private flipkartservice: Service1Service) {

  }
  ngOnInit(): void {
    this.Products();

  }


  Array: any[] = [];
  Electronics:any[]=[];
  Campus:any[]=[];
  Beauty:any[]=[];

  Products() {
    this.flipkartservice.GetProducts().subscribe(res => {
      this.Array = res;
      console.log(this.Array)
      this.Electronics=[res[6],res[2],res[4]];
      this.Campus=[res[13],res[10],res[9],res[8],res[11],res[12],res[7]];
      this.Beauty=[res[18],res[19],res[17],res[16],res[14],res[15]];
    })
  }








}
