import { Component } from '@angular/core';

@Component({
  selector: 'app-kart',
  templateUrl: './kart.component.html',
  styleUrls: ['./kart.component.css']
})
export class KartComponent {

}
<div class="container">
    <div class="kart">
        <div class="dashboard">
            <div class="flipkart" routerLink="" routerLinkActive="active"><a  >Flipkart</a></div>
            <div class="grocery" routerLink="price" routerLinkActive="active"><a >Grocery</a></div>
        </div>
        <router-outlet></router-outlet>
    </div>
</div>
